namespace Zsharp.Bitcoin.Tests
{
    using NBitcoin;
    using Xunit;

    public sealed class InputTests
    {
        readonly ConsensusFactory factory;
        readonly TxIn subject;

        public InputTests()
        {
            this.factory = Networks.Default.Mainnet.Consensus.ConsensusFactory;
            this.subject = this.factory.CreateTxIn();
        }

        [Fact]
        public void IsSigmaSpend_WithCoinbase_ShouldReturnFalse()
        {
            MakeCoinbase(this.subject);

            Assert.False(this.subject.IsSigmaSpend());
        }

        [Fact]
        public void IsSigmaSpend_WithNormal_ShouldReturnFalse()
        {
            MakeNormal(this.subject);

            Assert.False(this.subject.IsSigmaSpend());
        }

        [Fact]
        public void IsSigmaSpend_WithZerocoinSpend_ShouldReturnFalse()
        {
            MakeZerocoinSpend(this.subject);

            Assert.False(this.subject.IsSigmaSpend());
        }

        [Fact]
        public void IsSigmaSpend_WithSigmaSpend_ShouldReturnTrue()
        {
            MakeSigmaSpend(this.subject);

            Assert.True(this.subject.IsSigmaSpend());
        }

        [Fact]
        public void IsZerocoinSpend_WithCoinbase_ShouldReturnFalse()
        {
            MakeCoinbase(this.subject);

            Assert.False(this.subject.IsZerocoinSpend());
        }

        [Fact]
        public void IsZerocoinSpend_WithNormal_ShouldReturnFalse()
        {
            MakeNormal(this.subject);

            Assert.False(this.subject.IsZerocoinSpend());
        }

        [Fact]
        public void IsZerocoinSpend_WithZerocoinSpend_ShouldReturnTrue()
        {
            MakeZerocoinSpend(this.subject);

            Assert.True(this.subject.IsZerocoinSpend());
        }

        [Fact]
        public void IsZerocoinSpend_WithSigmaSpend_ShouldReturnFalse()
        {
            MakeSigmaSpend(this.subject);

            Assert.False(this.subject.IsZerocoinSpend());
        }

        [Fact]
        public void GetConsensusFactory_WhenInvoke_ShouldReturnZcoinVersion()
        {
            var result = this.subject.GetConsensusFactory();

            Assert.Same(this.factory, result);
        }

        static void MakeCoinbase(TxIn input, int height = 1)
        {
            input.ScriptSig = new Script(Op.GetPushOp(height));
            input.PrevOut = new OutPoint();
            input.Sequence = Sequence.Final;
        }

        static void MakeNormal(TxIn input)
        {
            input.ScriptSig = Script.FromHex("483045022100afa09c339d426261ccec787f531435cf064eb2d415ac0a816e61a6c806455ac902202c82f12ea350d0ad7ed27ed31e0817dbdb4b2abd293cf47ddc00327c88fbd7b9012103aea31de24d077dadba79e52670f3f7507f59a21c34b28d531b9c600d8c33dee1");
            input.PrevOut.Hash = uint256.Parse("7767ca65fa03e32f14fbf93ee5f459b20b4dbf3921c283710bdcc6db546b31bb");
            input.PrevOut.N = 271;
            input.Sequence = Sequence.Final;
        }

        static void MakeZerocoinSpend(TxIn input)
        {
            input.ScriptSig = Script.FromHex("");
            input.PrevOut = new OutPoint();
            input.Sequence = new Sequence(1200);
        }

        static void MakeSigmaSpend(TxIn input)
        {
            input.ScriptSig = Script.FromHex("");
            input.PrevOut.Hash = uint256.Zero;
            input.PrevOut.N = 1;
            input.Sequence = Sequence.Final;
        }
    }
}
